const header=document.querySelector(".header");window.onscroll=()=>{const e=window.scrollY;console.log(e),e>=100?header.classList.add("bg-color"):header.classList.remove("bg-color")};const open=document.getElementById("open"),popup_container=document.getElementById("popup-container"),close=document.getElementById("close");open.addEventListener("click",(()=>{popup_container.classList.add("show")})),close.addEventListener("click",(()=>{popup_container.classList.remove("show")}));const month_btn=document.getElementById("month-btn"),yr_btn=document.getElementById("yr-btn"),monthly_container=document.getElementById("monthly-container"),yrly_container=document.getElementById("yrly-container");month_btn.addEventListener("click",(()=>{monthly_container.classList.add("show"),yrly_container.classList.add("hide"),yrly_container.classList.remove("show")})),yr_btn.addEventListener("click",(()=>{yrly_container.classList.add("show"),monthly_container.classList.add("hide"),monthly_container.classList.remove("show")}));